import React, { createRef, useEffect, useRef, useState } from 'react';
import './App.css';
import Box from './components/Box';


export type FocusHandle = {
  start: () => void;
};

function App() {
  const ref = React.useRef<HTMLDivElement>(null);
  const [width, setWidth] = useState(0);
  const {coords, setCoords} = useState({x: 0, y: 0});

  useEffect(() => {
    if(ref.current) {
      console.log('ref.current.getBoundingClientRect()',ref.current.getBoundingClientRect())
      setWidth(ref.current.getBoundingClientRect().width);    

    }
  }, []);

  return (
    <div className="App">
      <Box boxBackground='#f5f6fa' ref={ref} width={width} titleColor='#192a56' coords={coords}>
       Box, который показывает ширину 
     </Box>
    </div>
  );
}

export default App;
